import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import {
    Box,
    Typography,
    Paper,
    Divider,
    Accordion,
    AccordionSummary,
    AccordionDetails,
    Button,
    Grid,
    Chip,
    Stack,
    Avatar,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import SchoolIcon from '@mui/icons-material/School';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';
import BusinessCenterIcon from '@mui/icons-material/BusinessCenter';
import AccountBalanceIcon from '@mui/icons-material/AccountBalance';

const courseData = {
    // ... your courseData object as before ...
};

const defaultCourse = {
    name: 'Course Not Found',
    icon: <EmojiEventsIcon sx={{ color: '#fff', fontSize: 36 }} />,
    avatarColor: '#bdbdbd',
    description: 'Sorry, we could not find details for this course.',
    duration: '-',
    eligibility: '-',
    admission: [],
    syllabus: [],
    faqs: [],
};

const SectionHeader = ({ children }) => (
    <Typography
        variant="h6"
        sx={{
            color: '#1565c0',
            mb: 1.5,
            mt: 3,
            fontWeight: 700,
            letterSpacing: 0.5,
            textTransform: 'uppercase',
            fontSize: '1.05rem',
        }}
    >
        {children}
    </Typography>
);

const CourseDetails = () => {
    const { id } = useParams();
    const course = courseData[id] || defaultCourse;

    // Dialog state for Apply Now form
    const [open, setOpen] = useState(false);
    const [form, setForm] = useState({ name: '', email: '', phone: '' });

    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission (API call, etc.)
        alert('Application submitted!');
        setOpen(false);
        setForm({ name: '', email: '', phone: '' });
    };

    return (
        <Box
            sx={{
                maxWidth: '1900px',
                mx: 'auto',
                my: 5,
                p: { xs: 1, sm: 2 },
                background: 'linear-gradient(135deg, #e3f2fd 0%, #fff 100%)',
                minHeight: '100vh',
            }}
        >
            <Paper
                elevation={6}
                sx={{
                    p: { xs: 2, sm: 4 },
                    borderRadius: 5,
                    background: 'linear-gradient(135deg, #fff 60%, #e3f2fd 100%)',
                }}
            >
                <Grid container spacing={3} alignItems="center">
                    <Grid item>
                        <Avatar
                            sx={{
                                bgcolor: course.avatarColor,
                                width: 72,
                                height: 72,
                                boxShadow: '0 4px 16px rgba(25, 118, 210, 0.15)',
                            }}
                        >
                            {course.icon}
                        </Avatar>
                    </Grid>
                    <Grid item xs>
                        <Typography
                            variant="h4"
                            sx={{
                                fontWeight: 800,
                                color: course.avatarColor,
                                letterSpacing: 1,
                                mb: 1,
                                fontFamily: 'Montserrat, Arial, sans-serif',
                            }}
                        >
                            {course.name}
                        </Typography>
                        <Stack direction="row" spacing={1} flexWrap="wrap">
                            <Chip
                                label={`Duration: ${course.duration}`}
                                color="primary"
                                variant="outlined"
                                sx={{ fontWeight: 600 }}
                            />
                            <Chip
                                label={`Eligibility: ${course.eligibility}`}
                                color="info"
                                variant="outlined"
                                sx={{ fontWeight: 600 }}
                            />
                        </Stack>
                    </Grid>
                </Grid>

                <Divider sx={{ my: 3 }} />

                <Typography
                    variant="body1"
                    sx={{
                        mb: 3,
                        color: '#37474f',
                        fontSize: '1.13rem',
                        lineHeight: 1.7,
                        fontWeight: 500,
                    }}
                >
                    {course.description}
                </Typography>

                <SectionHeader>Admission Process</SectionHeader>
                <Box sx={{ mb: 3 }}>
                    {course.admission.length > 0 ? (
                        <ol style={{ paddingLeft: 20, margin: 0 }}>
                            {course.admission.map((step, idx) => (
                                <li key={idx} style={{ marginBottom: 8, color: '#37474f', fontSize: '1.05rem' }}>
                                    {step}
                                </li>
                            ))}
                        </ol>
                    ) : (
                        <Typography sx={{ color: '#bdbdbd' }}>Admission details not available.</Typography>
                    )}
                </Box>

                <SectionHeader>Syllabus</SectionHeader>
                <Box sx={{ mb: 3 }}>
                    {course.syllabus && course.syllabus.length > 0 ? (
                        <TableContainer component={Paper} sx={{ boxShadow: 'none', mb: 2 }}>
                            <Table size="small">
                                <TableHead>
                                    <TableRow>
                                        <TableCell sx={{ fontWeight: 700, color: '#1565c0' }}>Subject</TableCell>
                                        <TableCell sx={{ fontWeight: 700, color: '#1565c0' }}>Topics</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {course.syllabus.map((row, idx) => (
                                        <TableRow key={idx}>
                                            <TableCell>{row.subject}</TableCell>
                                            <TableCell>{row.topics}</TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </TableContainer>
                    ) : (
                        <Typography sx={{ color: '#bdbdbd' }}>Syllabus not available.</Typography>
                    )}
                </Box>

                <SectionHeader>Frequently Asked Questions</SectionHeader>
                <Box sx={{ mb: 3 }}>
                    {course.faqs.length > 0 ? (
                        course.faqs.map((faq, idx) => (
                            <Accordion key={idx} sx={{ mb: 1, borderRadius: 2, boxShadow: 'none', border: '1px solid #e3e3e3' }}>
                                <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                                    <Typography sx={{ fontWeight: 600, color: '#1976d2' }}>{faq.q}</Typography>
                                </AccordionSummary>
                                <AccordionDetails>
                                    <Typography sx={{ color: '#37474f' }}>{faq.a}</Typography>
                                </AccordionDetails>
                            </Accordion>
                        ))
                    ) : (
                        <Typography sx={{ color: '#bdbdbd' }}>No FAQs available for this course.</Typography>
                    )}
                </Box>

                <Divider sx={{ my: 3 }} />

                {/* Apply Now Button and Dialog */}
                <Box sx={{ textAlign: 'center', mt: 2 }}>
                    <Button
                        variant="contained"
                        color="primary"
                        size="large"
                        sx={{
                            px: 5,
                            py: 1.5,
                            borderRadius: 3,
                            fontWeight: 700,
                            fontSize: '1.1rem',
                            boxShadow: '0 4px 16px rgba(25, 118, 210, 0.12)',
                            textTransform: 'none',
                            letterSpacing: 1,
                        }}
                        onClick={handleOpen}
                    >
                        Apply Now
                    </Button>
                </Box>

                <Dialog open={open} onClose={handleClose} maxWidth="xs" fullWidth>
                    <DialogTitle sx={{ textAlign: 'center', fontWeight: 700, color: '#1976d2', fontSize: '1.5rem' }}>
                        Apply for {course.name}
                    </DialogTitle>
                    <DialogContent sx={{ py: 3 }}>
                        <Box
                            component="form"
                            onSubmit={handleSubmit}
                            sx={{
                                display: 'flex',
                                flexDirection: 'column',
                                gap: 2,
                                mt: 1,
                            }}
                        >
                            <TextField
                                margin="dense"
                                required
                                fullWidth
                                label="Full Name"
                                name="name"
                                value={form.name}
                                onChange={handleChange}
                                variant="outlined"
                                InputProps={{
                                    style: { borderRadius: 12 }
                                }}
                                InputLabelProps={{
                                    style: { fontWeight: 500 }
                                }}
                                helperText="Enter your full name as per documents"
                            />
                            <TextField
                                margin="dense"
                                required
                                fullWidth
                                label="Email Address"
                                name="email"
                                type="email"
                                value={form.email}
                                onChange={handleChange}
                                variant="outlined"
                                InputProps={{
                                    style: { borderRadius: 12 }
                                }}
                                InputLabelProps={{
                                    style: { fontWeight: 500 }
                                }}
                                helperText="We'll never share your email."
                            />
                            <TextField
                                margin="dense"
                                required
                                fullWidth
                                label="Phone Number"
                                name="phone"
                                value={form.phone}
                                onChange={handleChange}
                                variant="outlined"
                                InputProps={{
                                    style: { borderRadius: 12 }
                                }}
                                InputLabelProps={{
                                    style: { fontWeight: 500 }
                                }}
                                helperText="Enter a valid mobile number"
                            />
                            <DialogActions sx={{ justifyContent: 'space-between', mt: 2 }}>
                                <Button onClick={handleClose} color="secondary" variant="outlined" sx={{ borderRadius: 2 }}>
                                    Cancel
                                </Button>
                                <Button type="submit" variant="contained" color="primary" sx={{ borderRadius: 2, px: 4 }}>
                                    Submit
                                </Button>
                            </DialogActions>
                        </Box>
                    </DialogContent>
                </Dialog>
            </Paper>
        </Box>
    );
};

export default CourseDetails;
