import React from 'react';
import { useParams } from 'react-router-dom';
import { Box, Typography, Paper, Divider, Accordion, AccordionSummary, AccordionDetails, Button, Grid, Chip } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import SchoolIcon from '@mui/icons-material/School';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';
import BusinessCenterIcon from '@mui/icons-material/BusinessCenter';
import AccountBalanceIcon from '@mui/icons-material/AccountBalance';

const courseData = {
  '11th-commerce': {
    name: '11th Commerce',
    icon: <SchoolIcon sx={{ color: '#1976d2', fontSize: 48 }} />,
    description: 'The 11th Commerce stream introduces students to the basics of business, economics, and accountancy, laying the foundation for future studies in commerce and management.',
    duration: '1 Year',
    eligibility: 'Passed 10th Grade from a recognized board.',
    admission: [
      'Fill out the online application form.',
      'Submit required documents (10th marksheet, ID proof).',
      'Attend counseling session.',
      'Pay admission fees to confirm your seat.'
    ],
    faqs: [
      {
        q: 'What subjects are covered in 11th Commerce?',
        a: 'Subjects include Accountancy, Business Studies, Economics, Mathematics, and English.'
      },
      {
        q: 'Is Mathematics compulsory?',
        a: 'Mathematics is optional but recommended for students interested in finance or economics.'
      }
    ]
  },
  'bcom': {
    name: 'B.Com',
    icon: <AccountBalanceIcon sx={{ color: '#0288d1', fontSize: 48 }} />,
    description: 'Bachelor of Commerce (B.Com) is a 3-year undergraduate program focusing on commerce, accounting, economics, and business law.',
    duration: '3 Years',
    eligibility: 'Passed 12th Commerce or equivalent.',
    admission: [
      'Apply online or visit the campus.',
      'Submit 12th marksheet and other documents.',
      'Clear entrance test (if applicable).',
      'Pay the first semester fees.'
    ],
    faqs: [
      {
        q: 'Can I pursue CA along with B.Com?',
        a: 'Yes, many students prepare for CA exams while pursuing B.Com.'
      },
      {
        q: 'Are internships available?',
        a: 'Yes, internships are encouraged during the course.'
      }
    ]
  },
  // Add more courses as needed...
};

const defaultCourse = {
  name: 'Course Not Found',
  icon: <EmojiEventsIcon sx={{ color: '#bdbdbd', fontSize: 48 }} />,
  description: 'Sorry, we could not find details for this course.',
  duration: '-',
  eligibility: '-',
  admission: [],
  faqs: []
};

const CourseDetails = () => {
  const { id } = useParams();
  const course = courseData[id] || defaultCourse;

  return (
    <Box sx={{ maxWidth: 800, mx: 'auto', my: 5, p: { xs: 2, sm: 4 } }}>
      <Paper elevation={4} sx={{ p: { xs: 2, sm: 4 }, borderRadius: 4 }}>
        <Grid container spacing={2} alignItems="center">
          <Grid item>{course.icon}</Grid>
          <Grid item xs>
            <Typography variant="h4" sx={{ fontWeight: 700, color: '#1976d2' }}>
              {course.name}
            </Typography>
            <Chip label={`Duration: ${course.duration}`} color="primary" sx={{ mt: 1, mr: 1 }} />
            <Chip label={`Eligibility: ${course.eligibility}`} color="info" sx={{ mt: 1 }} />
          </Grid>
        </Grid>
        <Divider sx={{ my: 3 }} />
        <Typography variant="body1" sx={{ mb: 3, color: '#455a64', fontSize: '1.1rem' }}>
          {course.description}
        </Typography>

        <Typography variant="h6" sx={{ color: '#1565c0', mb: 1 }}>
          Admission Process
        </Typography>
        <ol style={{ paddingLeft: 20, marginBottom: 24 }}>
          {course.admission.length > 0 ? (
            course.admission.map((step, idx) => (
              <li key={idx} style={{ marginBottom: 8, color: '#37474f' }}>{step}</li>
            ))
          ) : (
            <li style={{ color: '#bdbdbd' }}>Admission details not available.</li>
          )}
        </ol>

        <Typography variant="h6" sx={{ color: '#1565c0', mb: 1 }}>
          Frequently Asked Questions
        </Typography>
        {course.faqs.length > 0 ? (
          course.faqs.map((faq, idx) => (
            <Accordion key={idx} sx={{ mb: 1 }}>
              <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                <Typography sx={{ fontWeight: 600 }}>{faq.q}</Typography>
              </AccordionSummary>
              <AccordionDetails>
                <Typography>{faq.a}</Typography>
              </AccordionDetails>
            </Accordion>
          ))
        ) : (
          <Typography sx={{ color: '#bdbdbd', mb: 2 }}>No FAQs available for this course.</Typography>
        )}

        <Divider sx={{ my: 3 }} />

        <Box sx={{ textAlign: 'center', mt: 2 }}>
          <Button
            variant="contained"
            color="primary"
            size="large"
            sx={{
              px: 5,
              py: 1.5,
              borderRadius: 3,
              fontWeight: 700,
              fontSize: '1.1rem',
              boxShadow: '0 4px 16px rgba(25, 118, 210, 0.12)'
            }}
          >
            Apply Now
          </Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default CourseDetails;
