import React from 'react';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import StarIcon from '@mui/icons-material/Star';

const differences = [
    {
        title: "Regular B.Com",
        icon: <StarIcon sx={{ color: "#38bdf8", fontSize: 22, mt: "2px" }} />,
        color: "#38bdf8",
        outline: "#38bdf8"
    },
    {
        title: "Professional B.Com",
        icon: <CheckCircleIcon sx={{ color: "#6366f1", fontSize: 22, mt: "2px" }} />,
        color: "#6366f1",
        outline: "#6366f1"
    }
];

const points = [
    [
        "Focuses on general commerce subjects.",
        "Suitable for students seeking a broad understanding of commerce.",
        "Curriculum is less specialized.",
        "Ideal for those interested in careers like banking, accounting, or further studies (M.Com, MBA).",
        "Usually offered by most universities."
    ],
    [
        "Focuses on specialized subjects (e.g., B.Com (Honours), B.Com (Accounting & Finance), B.Com (Banking)).",
        "Designed for students aiming for specific professional careers.",
        "Curriculum includes industry-oriented and practical subjects.",
        "Often includes internships, projects, or professional certifications.",
        "May have higher eligibility criteria."
    ]
];

function BComDifference() {
    return (
        <div style={{
            fontFamily: 'Segoe UI, Arial, sans-serif',
            background: 'linear-gradient(135deg, #f8fafc 0%, #e0e7ff 100%)',
            minHeight: '100vh',
            padding: '2rem'
        }}>
            <h2 style={{
                textAlign: 'center',
                color: '#2d3748',
                marginBottom: '2rem',
                fontWeight: 700,
                letterSpacing: '1px'
            }}>
                Difference between Regular B.Com and Professional B.Com
            </h2>
            <div style={{
                display: 'flex',
                gap: '2rem',
                justifyContent: 'center',
                flexWrap: 'wrap'
            }}>
                {differences.map((type, i) => (
                    <div
                        key={type.title}
                        style={{
                            flex: '1 1 320px',
                            maxWidth: '400px',
                            background: '#fff',
                            border: 'none',
                            boxShadow: '0 4px 24px rgba(60,72,88,0.10)',
                            padding: '2rem 1.5rem',
                            borderRadius: '16px',
                            transition: 'transform 0.2s',
                            outline: `2px solid ${type.outline}`
                        }}
                    >
                        <h3 style={{
                            color: type.color,
                            fontWeight: 600,
                            marginBottom: '1rem',
                            textAlign: 'center'
                        }}>
                            {type.title}
                        </h3>
                        <ul style={{
                            listStyle: 'none',
                            padding: 0,
                            margin: 0
                        }}>
                            {points[i].map((point, idx) => (
                                <li key={idx} style={{
                                    marginBottom: '1rem',
                                    display: 'flex',
                                    alignItems: 'flex-start'
                                }}>
                                    <span style={{
                                        marginRight: '0.5em',
                                        marginTop: '2px'
                                    }}>
                                        {type.icon}
                                    </span>
                                    <span>{point}</span>
                                </li>
                            ))}
                        </ul>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default BComDifference;
