import React, { useRef, useEffect } from "react";
import './ImgSlider.css';
const images = [
    {
        src: "https://via.placeholder.com/600x300?text=Slide+1",
        description: "This is the first slide description.",
    },
    {
        src: "https://via.placeholder.com/600x300?text=Slide+2",
        description: "This is the second slide description.",
    },
    {
        src: "https://via.placeholder.com/600x300?text=Slide+3",
        description: "This is the third slide description.",
    },
];

const SLIDE_INTERVAL = 3000; // 3 seconds

export default function ImgSlider() {
    const [current, setCurrent] = React.useState(0);
    const timeoutRef = useRef(null);

    useEffect(() => {
        timeoutRef.current = setTimeout(() => {
            setCurrent((prev) => (prev + 1) % images.length);
        }, SLIDE_INTERVAL);
        return () => clearTimeout(timeoutRef.current);
    }, [current]);

    return (
        <div style={{ width: "600px", margin: "auto", overflow: "hidden" }}>
            <div
                style={{
                    display: "flex",
                    transition: "transform 0.5s ease",
                    transform: `translateX(-${current * 100}%)`,
                    height: "300px",
                }}
            >
                {images.map((img, idx) => (
                    <div
                        key={idx}
                        style={{
                            minWidth: "100%",
                            boxSizing: "border-box",
                            position: "relative",
                            height: "300px",
                        }}
                    >
                        <img
                            src={img.src}
                            alt={`slide-${idx}`}
                            style={{
                                width: "100%",
                                height: "300px",
                                objectFit: "cover",
                                display: "block",
                            }}
                        />
                        <div
                            style={{
                                position: "absolute",
                                bottom: "0",
                                left: "0",
                                width: "100%",
                                background: "rgba(0,0,0,0.5)",
                                color: "#fff",
                                padding: "10px",
                                boxSizing: "border-box",
                                fontSize: "1.1rem",
                            }}
                        >
                            {img.description}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
